import React from 'react';
import { connect } from '../../../common/ReduxDecorator';
import { StoreType } from '../../../common/resources/redux/types';
import { Typography, Pagination, Carousel } from 'antd';
import { DashboardStoreProps, DashboardProps } from './interfaces/dasboard.interfaces';
import data from './../../../common/data.json';

const { Title, Text } = Typography;
@connect(
    (store: StoreType): DashboardStoreProps => {
        if (!store) return ({} as unknown) as DashboardStoreProps;

        return {
            categories: store.sideNav.categories,
            brand: store.sideNav.brand,
            price: store.sideNav.price,
            color: store.sideNav.color
        }
    }
)

export default class Dashboard extends React.Component<DashboardStoreProps> {
    state = {
        currentPage: 1,
        postPerPage: 4,
        currentPost: [],
        getCategories: [],
        getBrand: [],
        getPrice: [],
        getColor: [],
    }

    componentDidMount = () => {
        this.getData()
    }

    componentDidUpdate = (prevprops: any) => {
        // const { getCategories, getBrand, getPrice, getColor } = this.props
        // const arr = [getCategories, getBrand, getPrice, getColor]
        // arr.map((v, k) => {
        //     if(v !== prevprops.v){
        //         this.setState({
        //             v : v
        //         })
        //     }
        // })

        if (this.props.getCategories !== prevprops.getCategories) {
            this.setState({
                getCategories: this.props.getCategories
            })
        }

        if (this.props.getBrand !== prevprops.getBrand) {
            this.setState({
                getBrand: this.props.getBrand
            })
        }

        if (this.props.getPrice !== prevprops.getPrice) {
            this.setState({
                getPrice: this.props.getPrice
            })
        }

        if (this.props.getColor !== prevprops.getColor) {
            this.setState({
                getColor: this.props.getColor
            })
        }

    }

    getData = () => {
        const { currentPage, postPerPage } = this.state
        const indexOfLastPost = currentPage * postPerPage
        const indexOfFirstPost = indexOfLastPost - postPerPage
        const currentPost = data.slice(indexOfFirstPost, indexOfLastPost)
        this.setState({
            currentPost: currentPost
        }, () => {
            console.log("data", currentPost)
        })
    }

    fetchMore = (num: any) => {
        this.setState({
            currentPage: num,
        }, () => {
            this.getData()
        })
    }

    getFilteredData = () => {
        const { currentPost, getCategories, getBrand }: any = this.state;

        return currentPost.filter((filteredData: any) => {
            let filter = true;
            if (getCategories.length !== 0 && !getCategories.includes(filteredData.Type)) {
                filter = false;
            }

            if (getBrand.length !== 0 && !getBrand.includes(filteredData.Brand)) {
                filter = false;
            }

            return filter;
        });
    }

    render() {
        const { currentPost, getCategories, getBrand, getPrice, getColor } = this.state;
        console.log("UUU", getCategories, getBrand, getPrice, getColor)
        // console.log(window.location.origin);
        const data = this.getFilteredData();
        return (
            <>
                <div style={{ display: "flex", flexWrap: "wrap" }}>
                    {console.log("99", currentPost, data)}
                    {data.map((v: any, k: number) => {
                        return (
                            <div style={{ width: "40%", margin: "20px" }} key={"post" + k}>
                                <Carousel autoplay>
                                    {
                                        v.pictures && v.pictures.map((pic: string, k: number) => {
                                            return (
                                                <div style={{ width: "100%" }} key={k + "pic"}>
                                                    <img src={window.location.origin + "/" + pic} style={{ backgroundImage: "fit-content" }}></img>
                                                </div>
                                            )
                                        })
                                    }
                                </Carousel>
                                <Title level={5} style={{ marginBottom: "0px" }}>{v.Brand}</Title>
                                <Text style={{ color: "rgba(0, 0, 0, 0.85)", fontWeight: "lighter" }}>{v.Type}</Text>
                                <Title level={5} style={{ marginTop: "0px" }}>Rs. {v.Price}</Title>
                            </div>
                        )
                    })}
                    <div style={{ display: "flex", flexDirection: "row-reverse", width: "100%" }}>
                        <Pagination defaultCurrent={1} total={data.length} pageSize={4} onChange={this.fetchMore} />
                    </div>
                </div>
            </>
        )
    }
}
