import { ActionType } from "../common/resources/redux/types";

interface ReducerState {
    getCategories: {},
    getBrand: {},
    getPrice: {},
    getColor: {},
}

export default function reducer(
    state: ReducerState = {
        getCategories: {},
        getBrand: {},
        getPrice: {},
        getColor: {},
    },
    action: ActionType
): ReducerState {
    switch (action.type) {
        case "SET_CATEGORIES": {
            return { ...state, getCategories: action.payload };
        }
        case "SET_BRAND": {
            return { ...state, getBrand: action.payload };
        }
        case "SET_PRICE": {
            return { ...state, getCategories: action.payload };
        }
        case "SET_COLOR": {
            return { ...state, getBrand: action.payload };
        }
    }
    return state;
}
