import React from "react";
import './appLayout.css'
import NestedRouter from "./../core/components/NestedRouter/NestedRouter";
import { AppStoreProps, AppProps } from "./App.interfaces";
import { Menu, Layout, Skeleton, Popover } from 'antd';
import styled from 'styled-components';
import TopNav from './../layouts';
import { StoreType } from '../../common/resources/redux/types';
import { connect } from './../../common/ReduxDecorator';
import { getApiConfig } from './../../common/appConfig'
import { Link } from "react-router-dom";
import xhttp from '../../common/utils/Network';
import { onProjectSelectAction } from './../../actions/IssueResolutionAction';
import Dashboard from "../screens/dashboard";

const { Header, Content, Sider } = Layout;
const Container = styled.div`
    border-radius : 20px;
    width: calc(100% - 30px);
    height : 100%;
    padding : 15px;
    margin : 15px;
    height : fit-content;
    box-shadow: 0px 5px 25px #52575D1A;;
    font-family : 'Noto Sans', sans-serif;
`

@connect(
    (store: StoreType): AppStoreProps => {
        if (!store) return ({} as unknown) as AppStoreProps;

        return {
            projectSelected: store.issueResolution.projectid,
            projectList: store.issueResolution.getProjectList
        }
    }
)
export default class AppLayout extends React.Component<AppProps> {
    state = {

    }

    render(): React.ReactNode {

        return (
            <>
                <Layout style={{ minHeight: '100vh' }}>
                    <Layout className="site-layout">
                        <Header>
                            <TopNav />
                        </Header>
                        <Content style={{ display: "flex", height: "calc(100vh - 64px)" }}>
                            <Layout className="site-layout-background" style={{ padding: '0px 0', width: "100%" }}>
                                <Sider width="80px">

                                </Sider>
                                <Content className="layout">
                                    <Container>
                                        <NestedRouter routes={this.props.routes} />
                                    </Container>
                                </Content>
                            </Layout>
                        </Content>
                    </Layout>
                </Layout>
                {/* <Dashboard /> */}
            </>
        )
    }
}
