import React from 'react';
import image from './../../../../img/ColoredShapes.svg';
import { Menu, Layout, Pagination, Carousel } from 'antd';
// import TopNav from '../../layouts/TopNav';
import data from './../../../common/data.json';
import styled from 'styled-components';
import NestedRouter from './../../core/components/NestedRouter/NestedRouter'

const { Header, Content, Sider } = Layout;
const Container = styled.div`
    border-radius : 20px;
    width: calc(100% - 30px);
    height : 100%;
    padding : 15px;
    margin : 15px;
    height : fit-content;
    box-shadow: 0px 5px 25px #52575D1A;;
    font-family : 'Noto Sans', sans-serif;
`

export default class Dashboard extends React.Component {
    state = {
        arr: ['', '', '', ''],
        currentPage: 1,
        postPerPage: 4,
        currentPost: []
    }

    componentDidMount = () => {



    }

    createData = () => {
        const { currentPage, postPerPage } = this.state
        const indexOfLastPost = currentPage * postPerPage
        const indexOfFirstPost = indexOfLastPost - postPerPage
        const currentPost = data.slice(indexOfFirstPost, indexOfLastPost)
        this.setState({
            currentPost: currentPost
        })
        console.log("data", currentPost)
    }

    fetchMore = (num: any) => {
        this.setState({
            currentPage: num,
        })
    }

    login = () => {

    }

    render() {
        const contentStyle: object = {
            height: '160px',
            color: '#fff',
            lineHeight: '160px',
            textAlign: 'center',
            background: '#364d79',
        };
        const { arr, currentPost } = this.state
        return (
            <>
                <div style={{ display: "flex", flexWrap: "wrap" }}>
                    {currentPost.map((v: any, k: number) => {
                        return (
                            <div style={{ width: "40%", margin: "20px" }} key={"post" + k}>
                                <Carousel autoplay>
                                    <div>
                                        <h3 style={contentStyle}>1</h3>
                                    </div>
                                    <div>
                                        <h3 style={contentStyle}>2</h3>
                                    </div>
                                    <div>
                                        <h3 style={contentStyle}>3</h3>
                                    </div>
                                    <div>
                                        <h3 style={contentStyle}>4</h3>
                                    </div>
                                </Carousel>
                            </div>
                        )
                    })}
                    <div style={{ display: "flex", flexDirection: "row-reverse", width: "100%" }}>
                        <Pagination defaultCurrent={1} total={data.length} pageSize={4} onChange={this.fetchMore} />
                    </div>
                </div>
            </>
        )
    }
}
