import React from "react";
import './appLayout.css';
import 'antd/dist/antd.css';

// import NestedRouter from "./../core/components/NestedRouter/NestedRouter";
import { AppStoreProps, AppProps } from "./App.interfaces";
import { Menu, Layout, Breadcrumb, Typography, Select } from 'antd';
import styled from 'styled-components';
import TopNav from '../layouts/TopNav';
// import { StoreType } from '../../common/resources/redux/types';
import Dashboard from "../screens/dashboard";
// import { connect } from './../../common/ReduxDecorator';
// import { getApiConfig } from './../../common/appConfig'
// import { Link } from "react-router-dom";
// import xhttp from '../../common/utils/Network';
// import { onProjectSelectAction } from './../../actions/IssueResolutionAction';
// import Dashboard from "../screens/dashboard";
import {
    MenuUnfoldOutlined,
    MenuFoldOutlined,
    UserOutlined,
    VideoCameraOutlined,
    UploadOutlined,
} from '@ant-design/icons';

const { Title, Text } = Typography;

const { Header, Content, Sider } = Layout;
const Container = styled.div`
    border-radius : 20px;
    width: calc(100% - 30px);
    height : 100%;
    padding : 15px;
    margin : 15px;
    height : fit-content;
    box-shadow: 0px 5px 25px #52575D1A;;
    font-family : 'Noto Sans', sans-serif;
`

// @connect(
//     (store: StoreType): AppStoreProps => {
//         if (!store) return ({} as unknown) as AppStoreProps;

//         return {
//             projectSelected: store.issueResolution.projectid,
//             projectList: store.issueResolution.getProjectList
//         }
//     }
// )
export default class AppLayout extends React.Component<AppProps> {
    state = {
        collapsed: true,
        topNavElement: ['Men', 'Women', 'Kids', 'Home&Living', 'Beauty']
    }

    render(): React.ReactNode {
        const { topNavElement } = this.state
        return (
            <>
                <Layout>
                    <Header className="header">
                        <TopNav />
                    </Header>
                    <Content style={{ padding: '0 50px' }}>
                        <Breadcrumb style={{ margin: '16px 0' }}>
                            <Breadcrumb.Item>Home</Breadcrumb.Item>
                            <Breadcrumb.Item>Clothing</Breadcrumb.Item>
                            <Breadcrumb.Item>Men T-shirts</Breadcrumb.Item>
                        </Breadcrumb>
                        <div><b>Men T-shirts </b> : 3456 items</div>
                        <div style={{ display: "flex" }}>
                            <Title level={4} style={{ width: "200px" }}>Features</Title>
                            <div style={{ marginLeft: "20px", display: "flex", justifyContent: "space-around" }}>
                                <Select></Select>
                                <Select></Select>
                                <Select></Select>
                            </div>

                        </div>


                        <Layout className="site-layout-background" style={{ padding: '24px 0' }}>
                            <Sider className="site-layout-background" width={200}>

                            </Sider>
                            <Content style={{ padding: '0 24px', minHeight: 280 }}>Content</Content>
                        </Layout>
                    </Content>
                </Layout >
            </>
        )
    }
}
