import React from 'react';
import { connect } from '../../../common/ReduxDecorator';
import { StoreType } from '../../../common/resources/redux/types';
import { Typography, Pagination, Carousel } from 'antd';
import { DashboardStoreProps, DashboardProps } from './interfaces/dasboard.interfaces';
import data from './../../../common/data.json';

const { Title, Text } = Typography;
@connect(
    (store: StoreType): DashboardStoreProps => {
        if (!store) return ({} as unknown) as DashboardStoreProps;

        return {
            getCategories: store.sideNav.getCategories,
            getBrand: store.sideNav.getBrand,
            getPrice: store.sideNav.getPrice,
            getColor: store.sideNav.getColor
        }
    }
)

export default class Dashboard extends React.Component<DashboardStoreProps> {
    state = {
        currentPage: 1,
        postPerPage: 4,
        currentPost: []
    }

    componentDidMount = () => {
        this.createData()
    }

    componentDidUpdate = (prevprops: any) => {
        const arr = [getCategories, getBrand, getPrice, getColor]
    }

    createData = () => {
        const { currentPage, postPerPage } = this.state
        const indexOfLastPost = currentPage * postPerPage
        const indexOfFirstPost = indexOfLastPost - postPerPage
        const currentPost = data.slice(indexOfFirstPost, indexOfLastPost)
        this.setState({
            currentPost: currentPost
        }, () => {
            console.log("data", currentPost)
        })
    }

    fetchMore = (num: any) => {
        this.setState({
            currentPage: num,
        }, () => {
            this.createData()
        })
    }

    render() {
        const { currentPost } = this.state;
        console.log(window.location.origin);
        return (
            <>
                <div style={{ display: "flex", flexWrap: "wrap" }}>
                    {currentPost.map((v: any, k: number) => {
                        return (
                            <div style={{ width: "40%", margin: "20px" }} key={"post" + k}>
                                <Carousel autoplay>
                                    {
                                        v.pictures && v.pictures.map((pic: string) => {
                                            return (
                                                <div style={{ width: "100%" }}>
                                                    <img src={window.location.origin + "/" + pic} style={{ backgroundImage: "fit-content" }}></img>
                                                </div>
                                            )
                                        })
                                    }
                                </Carousel>
                                <Title level={5} style={{ marginBottom: "0px" }}>{v.Brand}</Title>
                                <Text style={{ color: "rgba(0, 0, 0, 0.85)", fontWeight: "lighter" }}>{v.Type}</Text>
                                <Title level={5} style={{ marginTop: "0px" }}>Rs. {v.Price}</Title>
                            </div>
                        )
                    })}
                    <div style={{ display: "flex", flexDirection: "row-reverse", width: "100%" }}>
                        <Pagination defaultCurrent={1} total={data.length} pageSize={4} onChange={this.fetchMore} />
                    </div>
                </div>
            </>
        )
    }
}
